CREATE TABLE `user` (
  `user_id` int(10) NOT NULL auto_increment COMMENT 'ID',
  `user_name` varchar(10) character set gbk NOT NULL COMMENT '昵称',
  `user_password` varchar(50) NOT NULL COMMENT '密码',
  `user_sex` varchar(2) character set gbk NOT NULL COMMENT '性别',
  `user_birthday` date NOT NULL COMMENT '生日',
  `user_identity` int(1) NOT NULL default '1' COMMENT '身份（1：普通会员，2：版主，3：管理员，4：超级管理员）',
  `user_realname` varchar(10) character set gbk NOT NULL COMMENT '真是姓名',
  `user_email` varchar(50) NOT NULL COMMENT '邮件',
  `user_regtime` date NOT NULL COMMENT '注册时间',
