  `user_signature` varchar(200) character set gbk NOT NULL COMMENT '个性签名',
  `user_address` varchar(100) character set gb2312 NOT NULL COMMENT '住址',
  `user_zipcode` int(6) NOT NULL COMMENT '邮编',
  `user_qq` int(10) NOT NULL COMMENT 'QQ',
  `user_zodiac` varchar(2) character set gbk NOT NULL COMMENT '生肖',
  `user_constellation` varchar(2) character set gbk NOT NULL COMMENT '星座',
  `user_bloodtype` varchar(2) character set gbk NOT NULL default 'A' COMMENT '血型',
  `user_phone` int(11) NOT NULL COMMENT '电话',
  `user_profession` varchar(20) character set gbk NOT NULL COMMENT '职业',
  `user_xueli` varchar(10) character set gbk NOT NULL default '0' COMMENT '学历',
  `user_experience` int(10) NOT NULL default '0' COMMENT '论坛经验',
  PRIMARY KEY  (`user_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='用户表（包含管理员在内的所有用户）' AUTO_INCREMENT=11 ;

